mapper.select:
        select
Id, AcceptedAnswerId, AnswerCount, Body, ClosedDate, CommentCount, CommunityOwnedDate, CreationDate, FavoriteCount, LastActivityDate, LastEditDate, LastEditorDisplayName, LastEditorUserId, OwnerUserId, ParentId, PostTypeId, Score, Tags, Title, ViewCount
        from Posts


                and Id = #{id}


                and AcceptedAnswerId = #{acceptedanswerid}


                and AnswerCount = #{answercount}


                and Body = #{body}


                and ClosedDate = #{closeddate}


                and CommentCount = #{commentcount}


                and CommunityOwnedDate = #{communityowneddate}


                and CreationDate = #{creationdate}


                and FavoriteCount = #{favoritecount}


                and LastActivityDate = #{lastactivitydate}


                and LastEditDate = #{lasteditdate}


                and LastEditorDisplayName = #{lasteditordisplayname}


                and LastEditorUserId = #{lasteditoruserid}


                and OwnerUserId = #{owneruserid}


                and ParentId = #{parentid}


                and PostTypeId = #{posttypeid}


                and Score = #{score}


                and Tags = #{tags}


                and Title = #{title}


                and ViewCount = #{viewcount}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from Posts


        and Id = #{id}


